// Process Google Analytics Data
const gaData = $input.first().json;
let processedGA = [];

if (gaData.rows) {
  processedGA = gaData.rows.map(row => ({
    platform: 'Google Analytics',
    campaign: row[0],
    source: row[1],
    medium: row[2],
    sessions: parseInt(row[3]),
    users: parseInt(row[4]),
    pageviews: parseInt(row[5]),
    bounceRate: parseFloat(row[6]),
    avgSessionDuration: parseFloat(row[7])
  }));
}

return processedGA.map(item => ({ json: item }));

// Process Facebook Ads Data
const fbData = $input.first().json;
let processedFB = [];

if (fbData.data) {
  processedFB = fbData.data.map(campaign => {
    const conversions = campaign.actions ? 
      campaign.actions.find(action => action.action_type === 'purchase')?.value || 0 : 0;

    return {
      platform: 'Facebook Ads',
      campaign: campaign.campaign_name,
      impressions: parseInt(campaign.impressions),
      clicks: parseInt(campaign.clicks),
      ctr: parseFloat(campaign.ctr),
      spend: parseFloat(campaign.spend),
      conversions: parseInt(conversions),
      cpm: parseFloat(campaign.cpm),
      cpp: parseFloat(campaign.cpp)
    };
  });
}

return processedFB.map(item => ({ json: item }));

// Process Google Ads Data
const gadsData = $input.first().json;
let processedGAds = [];

if (gadsData.results) {
  processedGAds = gadsData.results.map(result => ({
    platform: 'Google Ads',
    campaign: result.campaign.name,
    impressions: parseInt(result.metrics.impressions),
    clicks: parseInt(result.metrics.clicks),
    ctr: parseFloat(result.metrics.ctr),
    cost: parseFloat(result.metrics.cost_micros) / 1000000, // Convert micros to actual cost
    conversions: parseInt(result.metrics.conversions)
  }));
}

return processedGAds.map(item => ({ json: item }));

// Process Twitter Data
const twitterData = $input.first().json;
let processedTwitter = [];

if (twitterData.data) {
  const totalEngagement = twitterData.data.reduce((sum, tweet) => {
    const metrics = tweet.public_metrics;
    return {
      tweets: sum.tweets + 1,
      likes: sum.likes + metrics.like_count,
      retweets: sum.retweets + metrics.retweet_count,
      replies: sum.replies + metrics.reply_count,
      quotes: sum.quotes + metrics.quote_count
    };
  }, { tweets: 0, likes: 0, retweets: 0, replies: 0, quotes: 0 });

  processedTwitter = [{
    platform: 'Twitter',
    totalTweets: totalEngagement.tweets,
    totalLikes: totalEngagement.likes,
    totalRetweets: totalEngagement.retweets,
    totalReplies: totalEngagement.replies,
    totalQuotes: totalEngagement.quotes,
    engagementRate: totalEngagement.tweets > 0 ? 
      ((totalEngagement.likes + totalEngagement.retweets + totalEngagement.replies) / totalEngagement.tweets).toFixed(2) : 0
  }];
}

return processedTwitter.map(item => ({ json: item }));

// Generate comprehensive marketing report
const allData = $input.all();
const reportDate = $('Set Date Range').item.json.reportDate;
const weekStart = $('Set Date Range').item.json.weekStart;
const weekEnd = $('Set Date Range').item.json.weekEnd;

let gaData = [], fbData = [], gadsData = [], twitterData = [];

// Separate data by platform
allData.forEach(input => {
  input.forEach(item => {
    const data = item.json;
    switch(data.platform) {
      case 'Google Analytics':
        gaData.push(data);
        break;
      case 'Facebook Ads':
        fbData.push(data);
        break;
      case 'Google Ads':
        gadsData.push(data);
        break;
      case 'Twitter':
        twitterData.push(data);
        break;
    }
  });
});

// Calculate totals
const totalSpend = [...fbData, ...gadsData].reduce((sum, item) => sum + (item.spend || item.cost || 0), 0);
const totalClicks = [...fbData, ...gadsData].reduce((sum, item) => sum + (item.clicks || 0), 0);
const totalImpressions = [...fbData, ...gadsData].reduce((sum, item) => sum + (item.impressions || 0), 0);
const totalConversions = [...fbData, ...gadsData].reduce((sum, item) => sum + (item.conversions || 0), 0);
const totalSessions = gaData.reduce((sum, item) => sum + (item.sessions || 0), 0);

// Generate report
const report = {
  reportGeneratedAt: new Date().toISOString(),
  reportPeriod: `${weekStart} to ${weekEnd}`,

  summary: {
    totalAdSpend: totalSpend.toFixed(2),
    totalClicks: totalClicks,
    totalImpressions: totalImpressions,
    totalConversions: totalConversions,
    totalSessions: totalSessions,
    overallCTR: totalImpressions > 0 ? ((totalClicks / totalImpressions) * 100).toFixed(2) + '%' : '0%',
    costPerConversion: totalConversions > 0 ? (totalSpend / totalConversions).toFixed(2) : 'N/A',
    conversionRate: totalClicks > 0 ? ((totalConversions / totalClicks) * 100).toFixed(2) + '%' : '0%'
  },

  platformBreakdown: {
    googleAnalytics: {
      totalSessions: totalSessions,
      totalUsers: gaData.reduce((sum, item) => sum + (item.users || 0), 0),
      totalPageviews: gaData.reduce((sum, item) => sum + (item.pageviews || 0), 0),
      avgBounceRate: gaData.length > 0 ? (gaData.reduce((sum, item) => sum + (item.bounceRate || 0), 0) / gaData.length).toFixed(2) + '%' : '0%',
      campaigns: gaData
    },

    facebookAds: {
      totalSpend: fbData.reduce((sum, item) => sum + (item.spend || 0), 0).toFixed(2),
      totalClicks: fbData.reduce((sum, item) => sum + (item.clicks || 0), 0),
      totalImpressions: fbData.reduce((sum, item) => sum + (item.impressions || 0), 0),
      totalConversions: fbData.reduce((sum, item) => sum + (item.conversions || 0), 0),
      campaigns: fbData
    },

    googleAds: {
      totalCost: gadsData.reduce((sum, item) => sum + (item.cost || 0), 0).toFixed(2),
      totalClicks: gadsData.reduce((sum, item) => sum + (item.clicks || 0), 0),
      totalImpressions: gadsData.reduce((sum, item) => sum + (item.impressions || 0), 0),
      totalConversions: gadsData.reduce((sum, item) => sum + (item.conversions || 0), 0),
      campaigns: gadsData
    },

    twitter: twitterData.length > 0 ? twitterData[0] : { message: 'No Twitter data available' }
  },

  topPerformingCampaigns: {
    byConversions: [...fbData, ...gadsData]
      .filter(item => item.conversions > 0)
      .sort((a, b) => b.conversions - a.conversions)
      .slice(0, 5),

    byROAS: [...fbData, ...gadsData]
      .filter(item => item.conversions > 0 && (item.spend || item.cost) > 0)
      .map(item => ({
        ...item,
        roas: (item.conversions * 50) / (item.spend || item.cost) // Assuming $50 average order value
      }))
      .sort((a, b) => b.roas - a.roas)
      .slice(0, 5)
  }
};

return [{ json: report }];

